* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}



.wrapper {
    position: fixed;
    left: 0;
    right: 0;
    bottom: 0;
    top: 0;
    z-index: -1;
    background: url("b264f1845e2cc07dc5bf8451acc71104.jpg") center center no-repeat;
    background-size: cover;
/*    filter: blur(10px);*/
/*    transform: scale(1.2);*/
}

.player {
    position: fixed;
    z-index: 10;
    top: 50%;
    left: 50%;
    transform: translate(-50%,-50%);
    border-radius: 4px;
    color: #ea8989;
    padding: 20px 40px;
    background: rgba(255,255,255,0.7);
    width: 500px;
}

.player .main {
    display: flex;
}
.player .buttons {
    margin-top: 10px;
}
.player .buttons .iconfont {
    font-size: 32px;
    margin-right: 2px;
    cursor: pointer;
    transition: all 0.3s;
}

.player .buttons .iconfont:hover{
    opacity: 0.7;
}
.player .text {
    margin-left: 16px;
}

.player .text h3 {
    margin-top: 14px;
    margin-bottom: 4px;
}

.player .text p {
    margin-top: 8px;
    margin-bottom: 12px;
}

.player .bar {

    height: 3px;
    background: #ccc;
    border-radius: 3px;
}
.player .bar .current {

    height: 100%;
    width: 0%;
    border-radius: 3px;
    background: currentColor;
    transition: width 1s;


}

.player .time {
    text-align: right;
    margin-top: 8px;
}
